// src/component/ProductForm.jsx
import React, { useState } from "react";
import "./ProductForm.css"; // à créer pour le style

const ProductForm = ({ onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    name: "",
    sku: "",
    price: "",
    stockQuantity: "",
    category: "",
    imageUrl: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
    onClose();
  };

  return (
    <div className="modal-overlay">
      <div className="modal-form">
        <h2>Ajouter un produit</h2>
        <form onSubmit={handleSubmit}>
          <input name="name" placeholder="Nom du produit" onChange={handleChange} required />
          <input name="sku" placeholder="SKU" onChange={handleChange} required />
          <input name="price" type="number" placeholder="Prix (€)" onChange={handleChange} required />
          <input name="stockQuantity" type="number" placeholder="Quantité" onChange={handleChange} required />
          <input name="category" placeholder="Catégorie" onChange={handleChange} />
          <input name="imageUrl" placeholder="URL de l'image" onChange={handleChange} />
          <div className="form-buttons">
            <button type="submit">Ajouter</button>
            <button type="button" onClick={onClose} className="cancel-btn">Annuler</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductForm;
