import React, { useState, useEffect } from "react";
import Layout from "../component/Layout";
import ApiService from "../service/ApiService";
import { useNavigate, useParams } from "react-router-dom";
import { X, Upload, Image as ImageIcon, ArrowLeft } from "lucide-react";

const AddEditProductPage = () => {
  const { productId } = useParams("");
  const [name, setName] = useState("");
  const [sku, setSku] = useState("");
  const [price, setPrice] = useState("");
  const [stockQuantity, setStockQuantity] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [description, setDescription] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [imageUrl, setImageUrl] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [categories, setCategories] = useState([]);
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const categoriesData = await ApiService.getAllCategory();
        setCategories(categoriesData.categories);
      } catch (error) {
        showMessage(
          error.response?.data?.message ||
            "Erreur lors du chargement des catÃ©gories: " + error
        );
      }
    };

    const fetchProductById = async () => {
      if (productId) {
        setIsEditing(true);
        setIsLoading(true);
        try {
          const productData = await ApiService.getProductById(productId);
          if (productData.status === 200) {
            setName(productData.product.name);
            setSku(productData.product.sku);
            setPrice(productData.product.price);
            setStockQuantity(productData.product.stockQuantity);
            setCategoryId(productData.product.categoryId);
            setDescription(productData.product.description);
            setImageUrl(productData.product.imageUrl);
          } else {
            showMessage(productData.message);
          }
        } catch (error) {
          showMessage(
            error.response?.data?.message ||
              "Erreur lors du chargement du produit: " + error
          );
        } finally {
          setIsLoading(false);
        }
      }
    };

    fetchCategories();
    if (productId) fetchProductById();
  }, [productId]);

  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => {
      setMessage("");
    }, 4000);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        showMessage("Veuillez sÃ©lectionner un fichier image valide");
        return;
      }
      
      // Validate file size (5MB max)
      if (file.size > 5 * 1024 * 1024) {
        showMessage("La taille de l'image ne peut pas dÃ©passer 5MB");
        return;
      }

      setImageFile(file);
      const reader = new FileReader();
      reader.onloadend = () => setImageUrl(reader.result);
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    const formData = new FormData();
    formData.append("name", name);
    formData.append("sku", sku);
    formData.append("price", price);
    formData.append("stockQuantity", stockQuantity);
    formData.append("categoryId", categoryId);
    formData.append("description", description);
    
    if (imageFile) {
      formData.append("imageFile", imageFile);
    }

    try {
      if (isEditing) {
        formData.append("productId", productId);
        await ApiService.updateProduct(formData);
        showMessage("Produit modifiÃ© avec succÃ¨s âœ…");
      } else {
        await ApiService.addProduct(formData);
        showMessage("Produit ajoutÃ© avec succÃ¨s ðŸŽ‰");
      }
      
      // Delay navigation to show success message
      setTimeout(() => {
        navigate("/product");
      }, 1500);
    } catch (error) {
      showMessage(
        error.response?.data?.message || "Erreur lors de la sauvegarde: " + error
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    navigate("/product");
  };

  const removeImage = () => {
    setImageFile(null);
    setImageUrl("");
  };

  if (isLoading && isEditing) {
    return (
      <Layout>
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Chargement du produit...</p>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="product-form-container">
        {message && (
          <div className={`message ${message.includes('succÃ¨s') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}

        <div className="form-header">
          <button className="back-btn" onClick={handleCancel} type="button">
            <ArrowLeft size={20} />
          </button>
          <h1>{isEditing ? "Modifier le produit" : "Ajouter un nouveau produit"}</h1>
        </div>

        <div className="form-card">
          <form onSubmit={handleSubmit} className="product-form">
            <div className="form-grid">
              {/* Product Name */}
              <div className="form-group full-width">
                <label className="form-label">
                  Nom du produit <span className="required">*</span>
                </label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Ex: MacBook Pro"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                />
              </div>

              {/* SKU */}
              <div className="form-group">
                <label className="form-label">
                  SKU <span className="required">*</span>
                </label>
                <input
                  type="text"
                  className="form-input"
                  placeholder="Ex: MBP-001"
                  value={sku}
                  onChange={(e) => setSku(e.target.value)}
                  required
                />
              </div>

              {/* Category */}
              <div className="form-group">
                <label className="form-label">
                  CatÃ©gorie <span className="required">*</span>
                </label>
                <select
                  className="form-select"
                  value={categoryId}
                  onChange={(e) => setCategoryId(e.target.value)}
                  required
                >
                  <option value="">SÃ©lectionner une catÃ©gorie</option>
                  {categories.map((category) => (
                    <option key={category.id} value={category.id}>
                      {category.name}
                    </option>
                  ))}
                </select>
              </div>

              {/* Quantity */}
              <div className="form-group">
                <label className="form-label">
                  QuantitÃ© <span className="required">*</span>
                </label>
                <input
                  type="number"
                  className="form-input"
                  placeholder="0"
                  value={stockQuantity}
                  onChange={(e) => setStockQuantity(e.target.value)}
                  min="0"
                  required
                />
              </div>

              {/* Price */}
              <div className="form-group">
                <label className="form-label">
                  Prix (â‚¬) <span className="required">*</span>
                </label>
                <input
                  type="number"
                  className="form-input"
                  placeholder="0.00"
                  value={price}
                  onChange={(e) => setPrice(e.target.value)}
                  min="0"
                  step="0.01"
                  required
                />
              </div>

              {/* Description */}
              <div className="form-group full-width">
                <label className="form-label">Description</label>
                <textarea
                  className="form-textarea"
                  placeholder="Description du produit..."
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows="4"
                />
              </div>

              {/* Image Upload */}
              <div className="form-group full-width">
                <label className="form-label">Image du produit</label>
                <div className="image-upload-container">
                  {!imageUrl ? (
                    <div className="image-upload-area">
                      <input
                        type="file"
                        id="image-upload"
                        className="image-input"
                        accept="image/*"
                        onChange={handleImageChange}
                      />
                      <label htmlFor="image-upload" className="image-upload-label">
                        <Upload size={24} />
                        <span>Cliquez pour tÃ©lÃ©charger une image</span>
                        <span className="upload-hint">PNG, JPG, JPEG (max 5MB)</span>
                      </label>
                    </div>
                  ) : (
                    <div className="image-preview-container">
                      <div className="image-preview-wrapper">
                        <img 
                          src={imageUrl} 
                          alt="AperÃ§u" 
                          className="image-preview"
                        />
                        <button 
                          type="button"
                          className="remove-image-btn"
                          onClick={removeImage}
                        >
                          <X size={16} />
                        </button>
                      </div>
                      <div className="image-actions">
                        <input
                          type="file"
                          id="image-change"
                          className="image-input"
                          accept="image/*"
                          onChange={handleImageChange}
                        />
                        <label htmlFor="image-change" className="change-image-btn">
                          <ImageIcon size={16} />
                          Changer l'image
                        </label>
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Form Actions */}
            <div className="form-actions">
              <button 
                type="button" 
                className="btn-cancel" 
                onClick={handleCancel}
                disabled={isLoading}
              >
                Annuler
              </button>
              <button 
                type="submit" 
                className="btn-submit" 
                disabled={isLoading}
              >
                {isLoading ? (
                  <div className="button-loading">
                    <div className="spinner"></div>
                    {isEditing ? "Modification..." : "Ajout..."}
                  </div>
                ) : (
                  isEditing ? "Modifier" : "Ajouter"
                )}
              </button>
            </div>
          </form>
        </div>
      </div>

      <style jsx>{`
