import React, { useState } from "react";
import Layout from "../component/Layout";
import ApiService from "../service/ApiService";
import "./ReceiveInventory.css"; // Ensure you create this CSS file for styling

const ReceiveInventoryPage = () => {
  const [formData, setFormData] = useState({
    product: "",
    supplier: "",
    quantity: "",
    notes: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await ApiService.receiveInventory(formData);
      alert("Inventory received successfully!");
      setFormData({ product: "", supplier: "", quantity: "", notes: "" });
    } catch (error) {
      alert(error.response?.data?.message || "Error receiving inventory");
    }
  };

  return (
    <Layout>
      <div className="inventory-form">
        <h2>Receive Inventory</h2>
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Product</label>
            <select 
              name="product" 
              value={formData.product}
              onChange={handleChange}
              required
            >
              <option value="">Select a product</option>
              {/* Populate with products from API */}
            </select>
          </div>

          <div className="form-group">
            <label>Supplier</label>
            <select 
              name="supplier" 
              value={formData.supplier}
              onChange={handleChange}
              required
            >
              <option value="">Select a supplier</option>
              {/* Populate with suppliers from API */}
            </select>
          </div>

          <div className="form-group">
            <label>Quantity</label>
            <input
              type="number"
              name="quantity"
              value={formData.quantity}
              onChange={handleChange}
              required
              min="1"
            />
          </div>

          <div className="form-group">
            <label>Notes</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleChange}
              rows="3"
            />
          </div>

          <button type="submit" className="submit-btn">
            Receive Inventory
          </button>
        </form>
      </div>
    </Layout>
  );
};

export default ReceiveInventoryPage;
