// src/components/Navbar.js

import React from "react";
// Utilisez NavLink au lieu de Link pour styliser le lien actif
import { NavLink, Link } from "react-router-dom";
import ApiService from "../service/ApiService";

// Importez les icônes que vous souhaitez utiliser
import { 
  FaTachometerAlt, 
  FaBoxOpen, 
  FaShoppingCart, 
  FaUsers,
  FaUserCircle,
  FaSignOutAlt
} from 'react-icons/fa';

// Importez le fichier CSS pour la navbar
import "./Navbar.css";

const logout = () => {
  ApiService.logout();
};

const Navbar = () => {
  const isAuth = ApiService.isAuthenticated();
  const isAdmin = ApiService.isAdmin();

  // Liste des liens pour rendre le code plus propre
  const navLinks = [
    { to: "/dashboard", text: "Tableau de bord", icon: <FaTachometerAlt />, show: isAuth },
    { to: "/product", text: "Produits", icon: <FaBoxOpen />, show: isAdmin },
    { to: "/purchase", text: "Commandes", icon: <FaShoppingCart />, show: isAuth },
    { to: "/supplier", text: "Fournisseurs", icon: <FaUsers />, show: isAdmin },
    // Vous pouvez ajouter ici les autres liens de votre sidebar originale
  ];

  return (
    <nav className="navbar">
      <Link to="/" className="navbar-logo">
        IMS
      </Link>

      <ul className="nav-menu">
        {navLinks.map((link, index) =>
          link.show && (
            <li key={index}>
              {/* NavLink ajoute une classe 'active' au lien correspondant à l'URL actuelle */}
              <NavLink to={link.to}>
                {link.icon}
                <span>{link.text}</span>
              </NavLink>
            </li>
          )
        )}
      </ul>
      
      <div className="nav-actions">
        {isAuth ? (
          <>
            <Link to="/profile" className="nav-action-link">
              <FaUserCircle /> <span>Profil</span>
            </Link>
            <Link onClick={logout} to="/login" className="nav-action-link">
              <FaSignOutAlt /> <span>Déconnexion</span>
            </Link>
          </>
        ) : (
          // Optionnel : liens à afficher si non connecté
          <Link to="/login">Connexion</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
