import React, { useEffect, useState } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from "recharts";

const DashboardPage = () => {
  const [message, setMessage] = useState("");
  const [selectedMonth, setSelectedMonth] = useState(new Date().getMonth() + 1);
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear());
  const [selectedData, setSelectedData] = useState("amount");
  const [transactionData, setTransactionData] = useState([]);

  // Sample data for demonstration
  useEffect(() => {
    const generateSampleData = () => {
      const daysInMonth = new Date(selectedYear, selectedMonth, 0).getDate();
      const data = [];
      
      for (let day = 1; day <= daysInMonth; day++) {
        data.push({
          day,
          count: Math.floor(Math.random() * 50) + 10,
          quantity: Math.floor(Math.random() * 200) + 50,
          amount: Math.floor(Math.random() * 10000) + 2000,
        });
      }
      return data;
    };

    setTransactionData(generateSampleData());
  }, [selectedMonth, selectedYear, selectedData]);

  const handleMonthChange = (e) => {
    setSelectedMonth(parseInt(e.target.value, 10));
  };

  const handleYearChange = (e) => {
    setSelectedYear(parseInt(e.target.value, 10));
  };

  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => {
      setMessage("");
    }, 4000);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      {message && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-6 max-w-md mx-auto">
          {message}
        </div>
      )}
      
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Dashboard Transactions</h1>
          <p className="text-gray-600">Analyse des données quotidiennes</p>
        </div>

        {/* Controls */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            
            {/* Data Type Buttons */}
            <div>
              <h3 className="text-lg font-semibold text-gray-700 mb-4">Type de données</h3>
              <div className="space-y-3">
                <button
                  onClick={() => setSelectedData("count")}
                  className={`w-full p-3 rounded-md text-left font-medium transition-colors ${
                    selectedData === "count"
                      ? "bg-blue-500 text-white"
                      : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                  }`}
                >
                  Nombre de Transactions
                </button>
                
                <button
                  onClick={() => setSelectedData("quantity")}
                  className={`w-full p-3 rounded-md text-left font-medium transition-colors ${
                    selectedData === "quantity"
                      ? "bg-green-500 text-white"
                      : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                  }`}
                >
                  Quantité de Produits
                </button>
                
                <button
                  onClick={() => setSelectedData("amount")}
                  className={`w-full p-3 rounded-md text-left font-medium transition-colors ${
                    selectedData === "amount"
                      ? "bg-purple-500 text-white"
                      : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                  }`}
                >
                  Montant
                </button>
              </div>
            </div>

            {/* Date Selectors */}
            <div>
              <h3 className="text-lg font-semibold text-gray-700 mb-4">Période</h3>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-600 mb-2">
                    Mois
                  </label>
                  <select
                    value={selectedMonth}
                    onChange={handleMonthChange}
                    className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    {Array.from({ length: 12 }, (_, i) => (
                      <option key={i + 1} value={i + 1}>
                        {new Date(0, i).toLocaleString("fr-FR", { month: "long" })}
                      </option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-600 mb-2">
                    Année
                  </label>
                  <select
                    value={selectedYear}
                    onChange={handleYearChange}
                    className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    {Array.from({ length: 5 }, (_, i) => {
                      const year = new Date().getFullYear() - i;
                      return (
                        <option key={year} value={year}>
                          {year}
                        </option>
                      );
                    })}
                  </select>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Chart */}
        <div className="bg-white rounded-lg shadow-md p-6">
          <h3 className="text-xl font-semibold text-gray-800 mb-6">
            Évolution quotidienne
          </h3>
          
          <div className="h-96 w-full">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={transactionData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb"/>
                <XAxis 
                  dataKey="day" 
                  stroke="#6b7280"
                  label={{ value: "Jour", position: "insideBottom", offset: -5 }}
                />
                <YAxis stroke="#6b7280"/>
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: 'white', 
                    border: '1px solid #d1d5db', 
                    borderRadius: '6px' 
                  }}
                />
                <Legend />
                <Line
                  type="monotone"
                  dataKey={selectedData}
                  stroke={
                    selectedData === "count" ? "#3b82f6" :
                    selectedData === "quantity" ? "#10b981" : "#8b5cf6"
                  }
                  strokeWidth={2}
                  dot={{ 
                    fill: selectedData === "count" ? "#3b82f6" :
                          selectedData === "quantity" ? "#10b981" : "#8b5cf6", 
                    strokeWidth: 2, 
                    r: 4
                  }}
                  name={
                    selectedData === "count" ? "Transactions" :
                    selectedData === "quantity" ? "Quantité" : "Montant"
                  }
                />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
